        createdAt: String!
        updatedAt: String!
    }

    type BookMetadata {
        book_id: ID!
        genres: [String]
        tags: [String]
        average_rating: Float
        ratings_count: Int
        page_count: Int
        language: String
        last_updated: String
    }

    type Review {
        id: ID!
        book_id: ID!
        user_id: ID!
        rating: Int!
        comment: String
        created_at: String!
    }

