    input BookInput {
        title: String!
        description: String
        published_date: String
        author_id: ID!
    }

    input BookUpdateInput {
        title: String
        description: String
        published_date: String
        author_id: ID
    }

    input BookMetadataInput {
        genres: [String]
        tags: [String]
        page_count: Int
        language: String
    }

    input ReviewInput {
        book_id: ID!
        rating: Int!
        comment: String
    }

    input BookFilterInput {
        title: String
        author_id: ID
        published_after: String
        published_before: String
        search: String
    }

    type Query {
        books(
            page: Int
