const url = require("url");

function cors(req, res, next) {
    let allowedOrigin = process.env.ALLOWED_DOMAINS;

    if (allowedOrigin !== "*") {
        allowedOrigin = allowedOrigin.split(",");

        const origin = req.get("origin") || "";
        const hostname = url.parse(origin).hostname;

        const domain = hostname
            ? hostname.split(".").slice(-2).join(".")
            : null;

        if (domain === process.env.DOMAIN || allowedOrigin.includes(domain)) {
            allowedOrigin = origin;
        }
    }

    res.header("Access-Control-Allow-Origin", allowedOrigin);
    res.header(
        "Access-Control-Allow-Headers",
        "Origin, X-Requested-With, Content-Type, Accept, Token, authorization"
    );
    res.header(
        "Access-Control-Expose-Headers",
        "Set-Token, Set-Universal-Token"
    );

    next();
}

module.exports = cors;
